import { useLocation } from 'react-router-dom';
import RecipeList from './RecipeList';

function UserHomePage(props) {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const creatorName = params.get('creator_name');

  const image = props.creator && creator.image;

  // Use the creatorName to render information about the creator and the recipes associated with the creator
    return (
        <div>
            <h1>{creatorName}</h1>
            <img src={image} alt={creatorName} />
            <RecipeList creatorName={creatorName} />
        </div>
    )
}

export default UserHomePage;
