import '../App.css';
import React, { useState, useLocation } from "react";
import NavBar from "./NavBar"
import { Switch, Route } from "react-router-dom";
import Recipes from "./Recipes"
import Creators from "./Creators"
import Search from "./Search"
import About from "./About"
import Home from "./Home"
import UserHomePage from "./UserHomePage"
import SHITS_BROKEN from './SHITS_BROKEN';
import RegistrationForm from './RegistrationForm';
// import { useHistory } from 'react-router-dom';

function App() {
  // const history = useHistory();
  // const [creatorName, setCreatorName] = useState('');

  const location = useLocation();

  function handleCreatorNameChange(creatorName) {
    // setCreatorName(creatorName);
    location.search = `?username=${creatorName}`;
  }

  return (
    <div>
      <NavBar />
        <Switch>
          <Route path="/recipes" >
            <Recipes creatorName={creatorName} />
          </Route>
          <Route path="/users" >
            <Creators handleCreatorNameChange={handleCreatorNameChange} />
          </Route>
          <Route path="/search">
            <Search />
          </Route>
          <Route path= "/about">
            <About />
          </Route>
          <Route path= "/create">
            <RegistrationForm />
          </Route>
          <Route exact path="/">
            <Home handleCreatorNameChange={handleCreatorNameChange} />
          </Route>
          <Route path= "/user">
            <UserHomePage />
          </Route>
          <SHITS_BROKEN />
        </Switch>
    </div>
  );
}

export default App;
