import { Link } from 'react-router-dom';

const LinkButton = (props) => {
  const { creatorName, onClick, ...otherProps } = props;
  return (
    <Link
      {...otherProps}
      to={{
        pathname: '/recipes',
        search: `?creator_name=${creatorName}`,
      }}
      onClick={() => {
        // Call the function passed as a prop to set the creator name in the parent component
        if (onClick) {
          onClick(creatorName);
        }
      }}
    >
      Recipes!
    </Link>
  );
};

export default LinkButton;
